INSERT INTO hats (id, name, description, image_path, price) VALUES (0, 'Summer Beach Sun Hat', 'Made of knitted lace ribbon with fabric material 100% cotton and sweatband inside helps to keep head cool.','sun-hat.jpg',34);
INSERT INTO hats (id, name, description, image_path, price) VALUES (1, 'Queenfur Knit Slouchy Beanie', 'The cable-knit hat is made of 100% soft Acrylic, with faux fur pompom on the top of the hat which is thick and comfortable. Can make you feel warm and fashion.', 'Beanie.jpg', 19);
INSERT INTO hats (id, name, description, image_path, price) VALUES (2, 'Bamboo Fashion Hat', 'The Bamboo Hat is pretied and requires no tying, just slip it on & off you go. Stretches to fit most head sizes.', 'bamboo-hat.jpg', 24);
INSERT INTO hats (id, name, description, image_path, price) VALUES (3, 'Fedora Hat', 'The shape of this hat falls effortlessly into place with grace.Wide brim hats make a stunning fashion statement, that can be worn with casual or formal attire.', 'Fedora Hat.jpg', 19);
INSERT INTO hats (id, name, description, image_path, price) VALUES (4, 'Wide Brim Sun Hat', 'Made of Eco-friendly Recycled paper. Breathable and comfortable. Tightly woven for the quality and durability. Strong Anti-uv Function, UPF 50+', 'Wide Brim Sun Hat.jpg', 34);
INSERT INTO hats (id, name, description, image_path, price) VALUES (5, 'Featherlight Sports Hat', 'The SAAKA Hat is designed to be unstructured unlike most hats which are firm and stiff. Our hat is completely flexible and never feels hard while being worn.', 'Featherlight Sports Hat.jpg', 35);
INSERT INTO hats (id, name, description, image_path, price) VALUES (6, 'Sun Visor Hat', 'Sun Visor Straw Hat for women with wide brim is made of high quality paper straw, gives you great sun protection all over the face and neck and the neutral colors go with everything.', 'Sun Visor Hat.jpg', 35);
INSERT INTO hats (id, name, description, image_path, price) VALUES (7, 'Cute Bucket Hat', 'Funny ocean multicolored pattern all over this bucket hat, makes you more attractive and charming the crowd. It is great for travel, beach, vacation, trip, outdoor activities, street strolling, fishing for the sun protection headwear.', 'Cute Bucket Hat.jpg', 20);
INSERT INTO hats (id, name, description, image_path, price) VALUES (8, 'Summer Sun Hat', 'If you looking for style and sun protection,this is the visor for you.It is very comfortable,5.1‚Äù wide brim provides excellent sun protection.The curved visor offers extra protection for your face and eyes from harmful sun rays.', 'Summer Sun Hat.jpg', 24);